// **Backend (Golang) – JWT Auth + REST API**

// main.go
package main

import (
	"github.com/gin-gonic/gin"
	"gorm.io/driver/[db_driver]"
	"gorm.io/gorm"
)

func main() {
	// Database
	db, err := gorm.Open([db_driver].Open("[db_connection_string]"), &gorm.Config{})
	if err != nil { panic(err) }
	db.AutoMigrate(&User{}, &Vehicle{}, &Company{})

	// Gin Router
	r := gin.Default()
	r.Use(JWTAuthMiddleware())

	// Routes
	r.POST("/register", registerHandler)
	r.POST("/login", loginHandler)
	r.GET("/vehicles", authMiddleware([role]), getVehiclesHandler)
	r.POST("/vehicles", authMiddleware([role]), addVehicleHandler)

	// Admin-only
	adminGroup := r.Group("/admin")
	adminGroup.Use(authMiddleware("admin"))
	adminGroup.GET("/users", getAllUsersHandler)

	r.Run(":[port]")
}

// **Frontend (Angular) – Role-Based UI**
// app.module.ts
import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { HttpClientModule, HTTP_INTERCEPTORS } from '@angular/common/http';
import { JWTInterceptor } from './interceptors/jwt.interceptor';

@NgModule({
  imports: [BrowserModule, HttpClientModule],
  providers: [
    { provide: HTTP_INTERCEPTORS, useClass: JWTInterceptor, multi: true }
  ]
})
export class AppModule {}

// **Models**
// user.model.ts
export interface User {
  id: number;
  username: string;
  password: string;
  role: '[role1]' | '[role2]' | '[role3]'; // admin, user, company
  vehicles?: Vehicle[];
}

export interface Vehicle {
  id: number;
  make: string;
  model: string;
  year: number;
  ownerId: number;
}

// **Services**
// auth.service.ts
@Injectable({ providedIn: 'root' })
export class AuthService {
  login(credentials: { username: string; password: string }) {
    return this.http.post('[api_url]/login', credentials);
  }
  register(user: Omit<User, 'id'>) {
    return this.http.post('[api_url]/register', user);
  }
}

